import React, { useState, useEffect } from "react";
import { mockGetUsers, mockAddUser, mockEditUser, mockDeleteUser } from "../services/mockApi";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [open, setOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({ id: null, name: "", role: "", status: "Active" });

  useEffect(() => {
    refreshUsers();
  }, []);

  const refreshUsers = () => {
    mockGetUsers().then((data) => {
      setUsers(data);
      setFilteredUsers(data);
    });
  };

  return (
    <div className="container">
      {/* Search Bar */}
      <div className="d-flex justify-content-between align-items-center mb-3">
        <input
          type="text"
          className="form-control w-50"
          placeholder="Search by name, role, or status"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button className="btn btn-primary" onClick={() => setOpen(true)}>
          <FontAwesomeIcon icon={faPlus} /> Add User
        </button>
      </div>

      {/* Users Table */}
      <table className="table table-bordered">
        <thead className="table-light">
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>{user.status}</td>
              <td>
                <button className="btn btn-warning btn-sm me-2" onClick={() => setOpen(true)}>
                  <FontAwesomeIcon icon={faEdit} /> Edit
                </button>
                <button className="btn btn-danger btn-sm" onClick={() => mockDeleteUser(user.id).then(() => refreshUsers())}>
                  <FontAwesomeIcon icon={faTrash} /> Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Users;
