// pages/Users.js
import React, { useState, useEffect } from 'react';
import { Box, Button, Table, TableHead, TableRow, TableCell, TableBody, Modal, TextField } from '@mui/material';
import { mockGetUsers, mockAddUser, mockEditUser, mockDeleteUser } from '../services/mockApi';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [open, setOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({ name: '', role: '', status: 'Active' });

  useEffect(() => {
    mockGetUsers().then((data) => setUsers(data));
  }, []);

  const handleSave = () => {
    if (currentUser.id) {
      mockEditUser(currentUser).then(() => refreshUsers());
    } else {
      mockAddUser(currentUser).then(() => refreshUsers());
    }
    setOpen(false);
  };

  const refreshUsers = () => {
    mockGetUsers().then((data) => setUsers(data));
  };

  return (
    <Box>
      <Button variant="contained" onClick={() => setOpen(true)}>
        Add User
      </Button>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell>Role</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {users.map((user) => (
            <TableRow key={user.id}>
              <TableCell>{user.name}</TableCell>
              <TableCell>{user.role}</TableCell>
              <TableCell>{user.status}</TableCell>
              <TableCell>
                <Button onClick={() => setCurrentUser(user)}>Edit</Button>
                <Button onClick={() => mockDeleteUser(user.id).then(() => refreshUsers())}>Delete</Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      <Modal open={open} onClose={() => setOpen(false)}>
        <Box>
          <TextField
            label="Name"
            value={currentUser.name}
            onChange={(e) => setCurrentUser({ ...currentUser, name: e.target.value })}
          />
          <TextField
            label="Role"
            value={currentUser.role}
            onChange={(e) => setCurrentUser({ ...currentUser, role: e.target.value })}
          />
          <Button onClick={handleSave}>Save</Button>
        </Box>
      </Modal>
    </Box>
  );
};

export default Users;
