// pages/Users.js
import React, { useState, useEffect } from "react";
import { mockGetUsers, mockAddUser, mockEditUser, mockDeleteUser } from "../services/mockApi";

const Users = () => {
  const [users, setUsers] = useState([]);
  const [open, setOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({ name: "", role: "", status: "Active" });

  useEffect(() => {
    mockGetUsers().then((data) => setUsers(data));
  }, []);

  const handleSave = () => {
    if (currentUser.id) {
      mockEditUser(currentUser).then(() => refreshUsers());
    } else {
      mockAddUser(currentUser).then(() => refreshUsers());
    }
    setOpen(false);
  };

  const refreshUsers = () => {
    mockGetUsers().then((data) => setUsers(data));
  };

  return (
    <div className="bg-white rounded shadow p-6">
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        onClick={() => setOpen(true)}
      >
        Add User
      </button>
      <table className="w-full mt-4 border-collapse border border-gray-200">
        <thead>
          <tr className="bg-gray-100">
            <th className="border p-2">Name</th>
            <th className="border p-2">Role</th>
            <th className="border p-2">Status</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id} className="hover:bg-gray-50">
              <td className="border p-2">{user.name}</td>
              <td className="border p-2">{user.role}</td>
              <td className="border p-2">{user.status}</td>
              <td className="border p-2">
                <button
                  className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600 mr-2"
                  onClick={() => setCurrentUser(user)}
                >
                  Edit
                </button>
                <button
                  className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
                  onClick={() => mockDeleteUser(user.id).then(() => refreshUsers())}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {open && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow">
            <h3 className="text-lg font-bold mb-4">Add/Edit User</h3>
            <input
              className="w-full p-2 border rounded mb-4"
              type="text"
              placeholder="Name"
              value={currentUser.name}
              onChange={(e) => setCurrentUser({ ...currentUser, name: e.target.value })}
            />
            <input
              className="w-full p-2 border rounded mb-4"
              type="text"
              placeholder="Role"
              value={currentUser.role}
              onChange={(e) => setCurrentUser({ ...currentUser, role: e.target.value })}
            />
            <button
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 mr-2"
              onClick={handleSave}
            >
              Save
            </button>
            <button
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
              onClick={() => setOpen(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Users;
