import React, { useState, useEffect } from "react";
import { getUsers, getRoles, updateUser } from "./mockApi";

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [roles, setRoles] = useState([]);

    useEffect(() => {
        getUsers().then(setUsers);
        getRoles().then(setRoles);
    }, []);

    const handleRoleChange = (userId, roleId) => {
        const user = users.find(user => user.id === userId);
        const role = roles.find(role => role.id === roleId);

        if (user && role) {
            const updatedUser = { ...user, role: role.name, permissions: role.permissions };
            const updatedUsers = users.map(u => (u.id === userId ? updatedUser : u));
            setUsers(updatedUsers);

            updateUser(userId, updatedUser).then(response => {
                if (!response.success) {
                    alert("Failed to update user");
                }
            });
        }
    };

    return (
        <div>
            <h1>User Management</h1>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Permissions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>
                                <select
                                    value={roles.find(role => role.name === user.role)?.id || ""}
                                    onChange={(e) => handleRoleChange(user.id, parseInt(e.target.value))}
                                >
                                    <option value="">Select Role</option>
                                    {roles.map(role => (
                                        <option key={role.id} value={role.id}>
                                            {role.name}
                                        </option>
                                    ))}
                                </select>
                            </td>
                            <td>
                                {user.permissions
                                    ? Object.entries(user.permissions)
                                          .map(([key, value]) => `${key}: ${value ? "✔" : "✘"}`)
                                          .join(", ")
                                    : "No Permissions"}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserManagement;
